mapper.select:
select count(*)
from fest
where area=#{area}

select * from 
	(select rownum as rnum, a.* from 
	(select * from fest where area=#{area} 
				order by festidx desc) a) b
where rnum>=#{start} and rnum<=#{end}

select * from 
	(select rownum as rnum, a.* from 
	(select * from fest where area=#{area} 
				order by festtitle asc) a) b
where rnum>=#{start} and rnum<=#{end}

select festimg
from fest
where festidx=#{festidx}
